$mobile-breakpoint: 768px;
$lg-breakpoint: 992px;
$xl-breakpoint: 1200px;
$spacer: 16px;

.headline {
    font-size: 2em;
    margin: 0px;
    padding-bottom: $spacer;
}

.item-col {
    margin-bottom: ($spacer * 2);
}

.item {
    height: 100%;
    position: relative;
    background-color: #f2f2f2;
    text-align: left;
    display: flex;
    flex-direction: column;

    .image {
        object-fit: contain;
        max-width: 100%;
        width: 100%;

      &-container {
        flex-shrink: 0;
      }
    }

    .title {
        font-size: 1.5em;
        font-weight: normal;
        margin: 0px;
        padding: $spacer $spacer 0px;
        word-break: break-word;
    }

    .subtitle {
        font-size: 1.25em;
        font-weight: normal;
        margin: 0px;
        padding: $spacer $spacer 0px;
    }

    .description {
        margin: 0px;
        padding: $spacer $spacer 0px;
        font-size: 1em;
        --description-length: 300;
    }

    .cta-container {
        display: flex;
        align-items: flex-end;
        height: 100%;

        .cta {
            font-size: 1em;
            color: #0041ff;
            text-decoration: none;
            padding: $spacer;
        }
    }
}

@media (max-width: $mobile-breakpoint) {
    .headline {
        text-align: center;
    }

    .item-col {
        text-align: center;
    }
}

